mapper.select:
select * 
from (select rownum rnum, b.*
	  from (select *
	  		from adminboard
	  		where key = 'FAQ'
	  		and adState = 'Y'
			order by adNum desc
	  ) b
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update adminboard
set adTitle = #{adTitle}, adContent = #{adContent}, QcateId = #{QcateId}
where adNum = #{adNum}

update adminboard
set adState = 'N'
where adNum = #{adNum}

mapper.select:
