mapper.update:
update seller
set waitState = 'Y', adminId = #{adminId}, waitOkDate = sysdate
where userId = #{userId}

update seller
set waitState = 'N', adminId = #{adminId}, waitOkDate = sysdate
where userId = #{userId}

mapper.select:
select * 
from (SELECT rownum rnum, b.*, statusid
	  FROM (select sickid, count(*) as sumcount
		  	from (select distinct sickid, warnnum
				  from boardwarn
				  union all
				  select distinct sickid, warnnum
				  from replywarn
			      )
		    group by sickid
	       ) b
	 INNER JOIN itda_user iu ON b.sickId = iu.userId
	 where rownum <= #{end}
	 )
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update itda_user
set statusid = 2
where userId = #{userId}

update boardwarn
set warnOk = 'Y'
where sickId = #{userId}

update replywarn
set warnOk = 'Y'
where sickId = #{userId}

update itda_user
set statusid = 3
where userId = #{userId}

update itda_user
set statusid = 1
where userId = #{userId}

mapper.select:
select rw.*, cb.chnum
from (select warnNum, punchId, sickId, replyNum, boardNum, 
		warnCateId, warnDate, warnReason 
		from replywarn 
		where sickid = #{sickId}
		and warnOk = 'N'
	 ) rw
left join chboard cb on rw.boardnum = cb.boardnum

select rw.*, cb.chnum
from (select warnNum, punchId, sickId, replyNum, boardNum, 
		warnCateId, warnDate, warnReason 
		from replywarn 
		where sickid = #{sickId}
	 ) rw
left join chboard cb on rw.boardnum = cb.boardnum

select * 
from (select rownum rnum, c.*
	  from (select *
	  		from Coupon
	  		where couponState = 'Y'
			order by couponCreate desc
	  ) c
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

select * 
from (select rownum rnum, c.*
	  from (select productName, productDetail, productPrice, productTerm
	  		from subproduct
			order by productPrice asc
	  ) c
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

